// Facebook login, pulled from https://developers.facebook.com/quickstarts/1520222368275265/?platform=web
(function(d, s, id){
     var js, fjs = d.getElementsByTagName(s)[0];
     if (d.getElementById(id)) {return;}
     js = d.createElement(s); js.id = id;
     js.src = "//connect.facebook.net/en_US/sdk.js";
     fjs.parentNode.insertBefore(js, fjs);
   }(document, 'script', 'facebook-jssdk'));

!function(){
    window.fbAsyncInit = function() {
        FB.init({
            // FB app ID for Malaria Assassins (generated by Facebook)
            appId: '1520222368275265', 
            // enable cookies to allow server to access the session
            cookie: true,
            version: 'v2.5'
        });

        $('#sign_in').click(function(e){
            e.preventDefault();
            FB.login(function(response){
                if(response.authResponse) {
                    window.location = '/auth/facebook/callback';
                }
            });
        });
        
        $("#sign_out").click(function(e){
            FB.getLoginStatus(function(response){
                if(response.authResponse){
                    FB.logout();
                }
            });            
        });
    };
};
